nuke_jq(function($)
{
	$('<div class="private-msg-overlay-inner"></div>').prependTo('body');
	$('<div class="private-msg-overlay"></div>').prependTo('body');	
    $('.private-msg-overlay').on('click', function()
    {
        $('.private-msg-overlay-inner').animate({
            top : '-=300',
            opacity : 0
        }, 400, function() {
            $('.private-msg-overlay').fadeOut(300);
            $(this).css('display','none');
        });
    });

	setTimeout(function()
    {
//---------------------------------------------------------------------
//  AUDIO ALERT SOUND
//---------------------------------------------------------------------
        if(pm_alert_sound == true)
        {
            // var audioElement = document.createElement('audio');
            // audioElement.setAttribute('src', pm_nukeurl+'/includes/js/jquery_pm_audio/YouGotMail.mp3');
            // audioElement.play(); 

            // console.log(pm_nukeurl+'/includes/js/jquery_pm_audio/george_carlin_there_is_a_letter_in_your_mailbox.mp3');    
        }
//---------------------------------------------------------------------
        $('.private-msg-overlay').css({
            'height' : $(document).height() ,
            'width' : $(document).width() ,
            'background-color' : pm_overlay_color
        }).fadeIn();

        $('.private-msg-overlay-inner').css({'box-shadow' : '0px 0px 49px 0px '+pm_button_color2});

        $('.private-msg-overlay-inner').css({
            'display' : 'block',
            'opacity' : '0',
            'top' : '-=300',
            'left' : '40%'
        }).animate({
            top : '40%',
            opacity : 1
        }, 600)
    }, pm_delay_timing+'000');

    var private_msg_overlay  = '<div class="private-msg-overlay-button">'
        private_msg_overlay += '  <span style="position: relative; top: 20px;">'+pm_alert_message+'</span>'
        private_msg_overlay += '</div>'
        private_msg_overlay += '<span class="private_msg_alert_message">'+pm_cookie_message+'</span>'
        private_msg_overlay += '<audio preload="auto" style="display:none" autoplay><source src="'+pm_nukeurl+'/includes/js/jquery_pm_audio/george_carlin_there_is_a_letter_in_your_mailbox.ogg" type="audio/ogg"><source src="'+pm_nukeurl+'/includes/js/jquery_pm_audio/george_carlin_there_is_a_letter_in_your_mailbox.mp3" type="audio/mpeg"></audio>';

	$(private_msg_overlay).prependTo('.private-msg-overlay-inner');

	$('div.private-msg-overlay-button').on('click', function()
    {
    	document.location.href = "modules.php?name=Private_Messages";
    });

});